import React from 'react'
import './Feature.css'
import Feature from '../../component/features/Feature';
import featureimg from '../../assets/image6.webp';

const featureData = [
  {
    title: 'Hormonal Changes and Follicular Phase:',
    text: "The menstrual cycle starts with the brain's hypothalamus producing GnRH, which signals the pituitary gland to release FSH and LH. These hormones stimulate the ovaries, leading to the development and release of an egg surrounded by a follicle.",
  },
  {
    title: 'Ovulation:',
    text: "Approximately on day 14 of a 28-day cycle, LH surge triggers ovulation, releasing the matured egg into the fallopian tube for potential fertilization.",
  },
  {
    title: 'Luteal Phase:',
    text: "After ovulation, the follicle becomes the corpus luteum, producing progesterone to prepare the uterine lining for pregnancy.",
  },
  {
    title: 'Menstruation (Period):',
    text: "If the egg is not fertilized, the corpus luteum disintegrates, causing a decline in hormone levels. This leads to the shedding of the uterine lining, known as menstruation or a period, lasting 2 to 7 days.",

  },
  {
    title: 'Menstrual Cycle Completion:',
    text: "After menstruation, the cycle restarts with the hypothalamus releasing GnRH. The menstrual cycle's duration varies from person to person, typically around 28 days, with cycles between 21 to 35 days considered normal.",

  },
];
const Process = () => {
  return (

    <div className="period__features-head section__padding" id="p&p">
      <h1 className="gradient__text">Menstrual Cycle </h1>
      <p>The menstrual cycle is a natural, monthly process that occurs in females of reproductive age. It is controlled by a complex interplay of hormones and involves various changes in the female reproductive system. The menstrual cycle's primary purpose is to prepare the body for potential pregnancy, but if conception does not occur, the cycle ends with menstruation.</p>
      <h2 className="gradient__text">Phases of the Menstrual Cycle:</h2>
      <div className="period__features ">

        <div className="period__features-box">
          <div className="period__features-box-image">
            <img src={featureimg} alt="Process" />
          </div>
        </div>
        <div className="period__features-container">
          {featureData.map((item, index) => (
            <Feature title={item.title} text={item.text} key={item.title + index} />
          ))}
        </div>
      </div>
    </div>
  )
}

export default Process
